import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function AdminDashboard() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [activeSection, setActiveSection] = useState("dashboard");

  // Homepage state
  const [homepage, setHomepage] = useState({
    title: "Democratic Students' Alliance Of Manipur",
    motto: "UNITE AND STRUGGLE TO SURVIVE",
    description: "DESAM was established in 2002...",
    image: ""
  });

  const [profile, setProfile] = useState({
    name: "Admin",
    email: "admin@gmail.com",
    photo: ""
  });
const navigate = useNavigate();




  // Login Handler
  const handleLogin = (e) => {
    e.preventDefault();

    if (email === "admin@gmail.com" && password === "1234") {
      setIsLoggedIn(true);
    } else {
      alert("Invalid Credentials");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setEmail("");
    setPassword("");
  };

  // Image preview handlers
  const handleHomepageImage = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setHomepage({ ...homepage, image: URL.createObjectURL(file) });
  };

  const handleProfilePhoto = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setProfile({ ...profile, photo: URL.createObjectURL(file) });
  };

  // üîê LOGIN SCREEN FIRST
  if (!isLoggedIn) {
    return (
      <div className="container d-flex justify-content-center align-items-center vh-100">
        <div className="card p-4 shadow" style={{ width: "350px" }}>
          <h4 className="text-center mb-3">Admin Login</h4>

          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <label>Email</label>
              <input
                type="email"
                className="form-control"
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="mb-3">
              <label>Password</label>
              <input
                type="password"
                className="form-control"
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button className="btn btn-primary w-100">
              Login
            </button>
          </form>
        </div>
      </div>
    );
  }


  
  // üü¢ AFTER LOGIN ‚Üí SHOW DASHBOARD
return (
  <div className="container-fluid p-0">
    <div className="row g-0">

      {/* üîπ Mobile Top Navbar */}
      <nav className="navbar navbar-dark bg-primary d-md-none">
        <div className="container-fluid">
          <span className="navbar-brand">DESAM Admin</span>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#sidebarMenu"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
        </div>
      </nav>

      {/* üîπ Sidebar */}
      <div
        className="col-md-3 col-lg-2 bg-primary text-white collapse d-md-block"
        id="sidebarMenu"
        style={{ minHeight: "100vh" }}
      >
        <div className="p-3">
          <h5
            className="cursor-pointer"
            onClick={() => navigate("/")}
          >
            DESAM Admin
          </h5>
          <hr />

          <button
            className="btn btn-link text-white d-block w-100 text-start"
            onClick={() => setActiveSection("dashboard")}
          >
            Dashboard
          </button>

          <button
            className="btn btn-link text-white d-block w-100 text-start"
            onClick={() => setActiveSection("homepage")}
          >
            Homepage
          </button>

          <button
            className="btn btn-link text-white d-block w-100 text-start"
            onClick={() => setActiveSection("profile")}
          >
            Profile
          </button>

          <button
            className="btn btn-link text-white d-block w-100 text-start"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </div>

      {/* üîπ Main Content */}
      <div className="col-md-9 col-lg-10 p-4">

        {activeSection === "dashboard" && (
          <>
            <h3>Dashboard Overview</h3>
            <div className="row mt-4">
              <div className="col-12 col-md-6 col-lg-4 mb-3">
                <div className="card shadow p-3 text-center">
                  <h5>Total Visitors</h5>
                  <h2>1200</h2>
                </div>
              </div>
            </div>
          </>
        )}

        {activeSection === "homepage" && (
          <>
            <h3>Edit Homepage</h3>
            <div className="card shadow p-4 mt-3">
              <div className="mb-3">
                <label>Website Title</label>
                <input
                  type="text"
                  className="form-control"
                  value={homepage.title}
                  onChange={(e) =>
                    setHomepage({ ...homepage, title: e.target.value })
                  }
                />
              </div>

              <div className="mb-3">
                <label>Upload Hero Image</label>
                <input
                  type="file"
                  className="form-control"
                  onChange={handleHomepageImage}
                />
              </div>

              {homepage.image && (
                <img
                  src={homepage.image}
                  alt="Preview"
                  className="img-fluid mb-3"
                  style={{ maxHeight: "200px" }}
                />
              )}

              <button className="btn btn-success w-100 w-md-auto">
                Save Changes
              </button>
            </div>
          </>
        )}

        {activeSection === "profile" && (
          <>
            <h3>Admin Profile</h3>
            <div className="card shadow p-4 mt-3 text-center">
              <img
                src={profile.photo || "https://via.placeholder.com/120"}
                alt="Profile"
                className="rounded-circle mb-3 img-fluid"
                width="120"
                height="120"
              />

              <input
                type="file"
                className="form-control mb-3"
                onChange={handleProfilePhoto}
              />

              <button className="btn btn-success w-100 w-md-auto">
                Update Profile
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  </div>
);
}

export default AdminDashboard;
