import React, { useState } from "react";
import { useNavigate } from "react-router-dom";




const [sidebarOpen, setSidebarOpen] = useState(false);

const menuItems = [
  { id: "dashboard", label: "Dashboard" },
  { id: "homepage", label: "Homepage" },
  { id: "profile", label: "Profile" },
];


function AdminDashboard() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [activeSection, setActiveSection] = useState("dashboard");

  // Homepage state
  const [homepage, setHomepage] = useState({
    title: "Democratic Students' Alliance Of Manipur",
    motto: "UNITE AND STRUGGLE TO SURVIVE",
    description: "DESAM was established in 2002...",
    image: ""
  });

  const [profile, setProfile] = useState({
    name: "Admin",
    email: "admin@gmail.com",
    photo: ""
  });
const navigate = useNavigate();




  // Login Handler
  const handleLogin = (e) => {
    e.preventDefault();

    if (email === "admin@gmail.com" && password === "1234") {
      setIsLoggedIn(true);
    } else {
      alert("Invalid Credentials");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setEmail("");
    setPassword("");
  };

  // Image preview handlers
  const handleHomepageImage = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setHomepage({ ...homepage, image: URL.createObjectURL(file) });
  };

  const handleProfilePhoto = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setProfile({ ...profile, photo: URL.createObjectURL(file) });
  };

  // üîê LOGIN SCREEN FIRST
  if (!isLoggedIn) {
    return (
      <div className="container d-flex justify-content-center align-items-center vh-100">
        <div className="card p-4 shadow" style={{ width: "350px" }}>
          <h4 className="text-center mb-3">Admin Login</h4>

          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <label>Email</label>
              <input
                type="email"
                className="form-control"
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="mb-3">
              <label>Password</label>
              <input
                type="password"
                className="form-control"
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button className="btn btn-primary w-100">
              Login
            </button>
          </form>
        </div>
      </div>
    );
  }


  
  // üü¢ AFTER LOGIN ‚Üí SHOW DASHBOARD
return (
  <div className="d-flex">

    {/* üîπ Sidebar */}
    <div
      className={`sidebar bg-primary text-white p-3 ${
        sidebarOpen ? "open" : ""
      }`}
    >
      <h5 className="mb-4">DESAM Admin</h5>

      {menuItems.map((item) => (
        <button
          key={item.id}
          className={`btn w-100 text-start mb-2 ${
            activeSection === item.id
              ? "btn-light text-dark"
              : "btn-link text-white"
          }`}
          onClick={() => {
            setActiveSection(item.id);
            setSidebarOpen(false); // close on mobile
          }}
        >
          {item.label}
        </button>
      ))}

      <hr />

      <button
        className="btn btn-danger w-100"
        onClick={handleLogout}
      >
        Logout
      </button>
    </div>

    {/* üîπ Overlay for mobile */}
    {sidebarOpen && (
      <div
        className="overlay"
        onClick={() => setSidebarOpen(false)}
      ></div>
    )}

    {/* üîπ Main Content */}
    <div className="content flex-grow-1">

      {/* Top Navbar */}
      <div className="bg-primary text-white p-3 d-flex justify-content-between align-items-center">
        <button
          className="btn btn-light"
          onClick={() => setSidebarOpen(!sidebarOpen)}
        >
          ‚ò∞
        </button>
        <h5 className="m-0">Admin Dashboard</h5>
      </div>

      <div className="p-4">

        {activeSection === "dashboard" && (
          <h3>Dashboard Overview</h3>
        )}

        {activeSection === "homepage" && (
          <h3>Edit Homepage</h3>
        )}

        {activeSection === "profile" && (
          <h3>Admin Profile</h3>
        )}

      </div>
    </div>
  </div>
);
}

export default AdminDashboard;
