import React, { useContext, useState } from "react";
import { ContentContext } from "../ContentContext";

export default function AdminDashboard() {
  const { content, setContent } = useContext(ContentContext);
  const [localContent, setLocalContent] = useState(content);
  const [isPreview, setIsPreview] = useState(false);

  // Handle field changes
  const handleChange = (section, key, value) => {
    setLocalContent({
      ...localContent,
      [section]: {
        ...localContent[section],
        [key]: value
      }
    });
  };

  // Save changes to context
  const saveChanges = () => {
    setContent(localContent);
    alert("Website content updated!");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Admin Dashboard</h1>
      <button
        onClick={() => setIsPreview(!isPreview)}
        style={{
          padding: "10px 20px",
          marginBottom: "20px",
          cursor: "pointer",
          backgroundColor: "#4CAF50",
          color: "#fff",
          border: "none",
          borderRadius: "5px"
        }}
      >
        {isPreview ? "Edit Mode" : "Preview Mode"}
      </button>

      {isPreview ? (
        // PREVIEW MODE
        <div style={{ border: "1px solid #ccc", padding: "20px" }}>
          <h2>Navbar</h2>
          <p>Logo: {localContent.navbar.logoText}</p>
          <p>Menu: {localContent.navbar.menuItems.join(", ")}</p>

          <h2>Home</h2>
          <h3>{localContent.home.heading}</h3>
          <p>{localContent.home.subheading}</p>
          <img
            src={localContent.home.heroImage}
            alt="Hero"
            style={{ maxWidth: "300px" }}
          />

          <h2>About</h2>
          <h3>{localContent.about.title}</h3>
          <p>{localContent.about.description}</p>

          <h2>Services</h2>
          {localContent.services.items.map((item, idx) => (
            <div key={idx}>
              <strong>{item.title}</strong>: {item.description}
            </div>
          ))}

          <h2>Career</h2>
          {localContent.career.positions.map((pos, idx) => (
            <div key={idx}>
              <strong>{pos.role}</strong> - {pos.description}
            </div>
          ))}

          <h2>Footer</h2>
          <p>{localContent.footer.text}</p>
        </div>
      ) : (
        // EDIT MODE
        <div style={{ display: "flex", flexDirection: "column", gap: "20px" }}>
          {/* Navbar Section */}
          <div>
            <h2>Navbar</h2>
            <input
              type="text"
              value={localContent.navbar.logoText}
              onChange={(e) => handleChange("navbar", "logoText", e.target.value)}
              placeholder="Logo Text"
              style={{ padding: "5px", width: "300px" }}
            />
            {localContent.navbar.menuItems.map((item, idx) => (
              <input
                key={idx}
                type="text"
                value={item}
                onChange={(e) => {
                  const newItems = [...localContent.navbar.menuItems];
                  newItems[idx] = e.target.value;
                  handleChange("navbar", "menuItems", newItems);
                }}
                placeholder={`Menu Item ${idx + 1}`}
                style={{ padding: "5px", marginLeft: "5px" }}
              />
            ))}
          </div>

          {/* Home Section */}
          <div>
            <h2>Home</h2>
            <input
              type="text"
              value={localContent.home.heading}
              onChange={(e) => handleChange("home", "heading", e.target.value)}
              placeholder="Heading"
              style={{ padding: "5px", width: "300px" }}
            />
            <input
              type="text"
              value={localContent.home.subheading}
              onChange={(e) => handleChange("home", "subheading", e.target.value)}
              placeholder="Subheading"
              style={{ padding: "5px", width: "300px", marginTop: "5px" }}
            />
            <input
              type="text"
              value={localContent.home.heroImage}
              onChange={(e) => handleChange("home", "heroImage", e.target.value)}
              placeholder="Hero Image URL"
              style={{ padding: "5px", width: "300px", marginTop: "5px" }}
            />
          </div>

          {/* About Section */}
          <div>
            <h2>About</h2>
            <input
              type="text"
              value={localContent.about.title}
              onChange={(e) => handleChange("about", "title", e.target.value)}
              placeholder="Title"
              style={{ padding: "5px", width: "300px" }}
            />
            <textarea
              value={localContent.about.description}
              onChange={(e) => handleChange("about", "description", e.target.value)}
              placeholder="Description"
              style={{ padding: "5px", width: "300px", height: "80px", marginTop: "5px" }}
            />
          </div>

          {/* Services Section */}
          <div>
            <h2>Services</h2>
            {localContent.services.items.map((item, idx) => (
              <div key={idx} style={{ marginBottom: "10px" }}>
                <input
                  type="text"
                  value={item.title}
                  onChange={(e) => {
                    const newItems = [...localContent.services.items];
                    newItems[idx].title = e.target.value;
                    handleChange("services", "items", newItems);
                  }}
                  placeholder="Service Title"
                  style={{ padding: "5px", width: "250px" }}
                />
                <input
                  type="text"
                  value={item.description}
                  onChange={(e) => {
                    const newItems = [...localContent.services.items];
                    newItems[idx].description = e.target.value;
                    handleChange("services", "items", newItems);
                  }}
                  placeholder="Service Description"
                  style={{ padding: "5px", width: "250px", marginLeft: "5px" }}
                />
              </div>
            ))}
          </div>

          {/* Career Section */}
          <div>
            <h2>Career</h2>
            {localContent.career.positions.map((pos, idx) => (
              <div key={idx} style={{ marginBottom: "10px" }}>
                <input
                  type="text"
                  value={pos.role}
                  onChange={(e) => {
                    const newPositions = [...localContent.career.positions];
                    newPositions[idx].role = e.target.value;
                    handleChange("career", "positions", newPositions);
                  }}
                  placeholder="Role"
                  style={{ padding: "5px", width: "200px" }}
                />
                <input
                  type="text"
                  value={pos.description}
                  onChange={(e) => {
                    const newPositions = [...localContent.career.positions];
                    newPositions[idx].description = e.target.value;
                    handleChange("career", "positions", newPositions);
                  }}
                  placeholder="Description"
                  style={{ padding: "5px", width: "250px", marginLeft: "5px" }}
                />
              </div>
            ))}
          </div>

          {/* Footer Section */}
          <div>
            <h2>Footer</h2>
            <input
              type="text"
              value={localContent.footer.text}
              onChange={(e) => handleChange("footer", "text", e.target.value)}
              placeholder="Footer Text"
              style={{ padding: "5px", width: "300px" }}
            />
          </div>

          <button
            onClick={saveChanges}
            style={{
              padding: "10px 20px",
              marginTop: "20px",
              backgroundColor: "#2196F3",
              color: "#fff",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer"
            }}
          >
            Save Changes
          </button>
        </div>
      )}
    </div>
  );
}
