import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { lazy, Suspense, useState, useEffect } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.css";

const Home = lazy(() => import("./ui/Home"));
const Career = lazy(() => import("./ui/Career"));
const Contact = lazy(() => import("./ui/Contact"));
const About = lazy(() => import("./ui/About"));
const Service = lazy(() => import("./ui/Service"));
const AdminDashboard = lazy(() => import("./components/AdminDashboard"));
import AdminPage from "./components/AdminPage"; // ✅ your login form

function App() {
  // ✅ Admin Authentication State
  const [isAuth, setIsAuth] = useState(false);

  // ✅ Check if admin already logged in (localStorage)
  useEffect(() => {
    const admin = localStorage.getItem("adminAuth");
    if (admin === "true") {
      setIsAuth(true);
    }
  }, []);

  return (
    <Router>
      <Suspense fallback={<div>Loading...</div>}>
        <Routes>
          {/* ✅ Public Routes */}
          <Route path="/" element={<Home isAuth={isAuth} />} />
          <Route path="about" element={<About />} />
          <Route path="services" element={<Service />} />
          <Route path="careers" element={<Career />} />
          <Route path="contact" element={<Contact />} />

          {/* ✅ Admin login route */}
          <Route path="admin" element={<AdminPage />} />

          {/* ✅ Protected Admin Route */}
          <Route
            path="AdminDashboard"
            element={
              isAuth ? (
                <AdminDashboard setIsAuth={setIsAuth} />
              ) : (
                <Navigate to="/admin" /> // redirect to login if not authenticated
              )
            }
          />

          {/* Redirect unknown routes */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Suspense>
    </Router>
  );
}

export default App;
