import React, { useState, useEffect } from "react";

const AdminCMS = () => {
  const [isAuth, setIsAuth] = useState(false);
  const [password, setPassword] = useState("");

  const [view, setView] = useState("dashboard"); // dashboard | editTeam

  const [team, setTeam] = useState([]);
  const [name, setName] = useState("");
  const [role, setRole] = useState("");
  const [image, setImage] = useState("");

  // Check login on load
  useEffect(() => {
    const auth = localStorage.getItem("adminAuth");
    if (auth === "true") {
      setIsAuth(true);
    }

    const savedTeam = JSON.parse(localStorage.getItem("teamData"));
    if (savedTeam) {
      setTeam(savedTeam);
    }
  }, []);

  const handleLogin = (e) => {
    e.preventDefault();

    if (password === "admin123") {
      localStorage.setItem("adminAuth", "true");
      setIsAuth(true);
    } else {
      alert("Wrong Password");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminAuth");
    setIsAuth(false);
  };

  const saveTeam = (updatedTeam) => {
    localStorage.setItem("teamData", JSON.stringify(updatedTeam));
    setTeam(updatedTeam);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImage(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleAddMember = () => {
    if (!name || !role || !image) {
      alert("Fill all fields");
      return;
    }

    const newMember = {
      id: Date.now(),
      name,
      role,
      image,
    };

    const updatedTeam = [...team, newMember];
    saveTeam(updatedTeam);

    setName("");
    setRole("");
    setImage("");
  };

  const handleDelete = (id) => {
    const updatedTeam = team.filter((m) => m.id !== id);
    saveTeam(updatedTeam);
  };

  // ================= LOGIN SCREEN =================
  if (!isAuth) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-100">
        <form
          onSubmit={handleLogin}
          className="bg-white p-8 rounded-2xl shadow-lg w-96"
        >
          <h2 className="text-2xl font-bold text-center mb-6">
            Admin Login
          </h2>

          <input
            type="password"
            placeholder="Enter Password"
            className="w-full border p-3 rounded mb-4"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <button className="w-full bg-blue-600 text-white py-3 rounded">
            Login
          </button>
        </form>
      </div>
    );
  }

  // ================= DASHBOARD =================
  if (view === "dashboard") {
    return (
      <div className="p-10 min-h-screen bg-gray-100">
        <h1 className="text-3xl font-bold mb-8">Admin Dashboard</h1>

        <div className="space-y-4">
          <button
            onClick={() => setView("editTeam")}
            className="bg-green-600 text-white px-6 py-3 rounded"
          >
            Edit Team Section
          </button>

          <button
            onClick={handleLogout}
            className="bg-red-600 text-white px-6 py-3 rounded"
          >
            Logout
          </button>
        </div>
      </div>
    );
  }

  // ================= EDIT TEAM =================
  return (
    <div className="p-10 min-h-screen bg-gray-100">
      <h2 className="text-3xl font-bold mb-6">Edit Team</h2>

      <div className="bg-white p-6 rounded-xl shadow mb-8">
        <h3 className="text-xl font-semibold mb-4">
          Add New Member
        </h3>

        <div className="space-y-4">
          <input
            type="text"
            placeholder="Name"
            className="w-full border p-3 rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <input
            type="text"
            placeholder="Role"
            className="w-full border p-3 rounded"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          />

          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="w-full"
          />

          <button
            onClick={handleAddMember}
            className="bg-blue-600 text-white px-6 py-2 rounded"
          >
            Add Member
          </button>
        </div>
      </div>

      <div className="grid md:grid-cols-3 gap-6">
        {team.map((member) => (
          <div key={member.id} className="bg-white p-4 rounded-xl shadow">
            <img
              src={member.image}
              alt={member.name}
              className="w-full h-48 object-cover rounded-lg"
            />
            <h3 className="text-lg font-bold mt-3">
              {member.name}
            </h3>
            <p className="text-gray-600">{member.role}</p>

            <button
              onClick={() => handleDelete(member.id)}
              className="mt-3 bg-red-500 text-white px-4 py-1 rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      <button
        onClick={() => setView("dashboard")}
        className="mt-8 bg-gray-700 text-white px-6 py-2 rounded"
      >
        Back to Dashboard
      </button>
    </div>
  );
};

export default AdminCMS;
