import React, { useEffect, useState } from "react";
import AOS from "aos";
import "aos/dist/aos.css";

import wakat from "../img/wakat.jpg";
import abtHeader from "../img/about-header.jpg";

import InnerHeaderBanner from "../components/InnerHeaderBanner";
import InnerHeader from "../components/InnerHeader";
import Footer from "../components/Footer";

const About = () => {
  const isAdmin = localStorage.getItem("adminAuth") === "true";

  const [editMode, setEditMode] = useState(false);

  const [aboutText, setAboutText] = useState([
    "Aphaba Maheiroi",
    "Aphaba Khunai",
    "Aphaba Tunglamchat ki Meerol ama semba haiba cda.",
    "Lairik tamba hourakpada chumna sengna wachat wanom yaodaba corruption yaodana achumba lambida Lairik tamhansi."
  ]);

  const [vision, setVision] = useState("To bring quality education in Manipur");
  const [mission, setMission] = useState("UNITE AND STRUGGLE TO SURVIVE");
  const [values, setValues] = useState("Driven by creativity, resourcefulness, and consumer focus.");

  const [team, setTeam] = useState([]);

  const [newName, setNewName] = useState("");
  const [newRole, setNewRole] = useState("");
  const [newImage, setNewImage] = useState("");

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });

    const savedData = JSON.parse(localStorage.getItem("aboutData"));
    if (savedData) {
      setAboutText(savedData.aboutText);
      setVision(savedData.vision);
      setMission(savedData.mission);
      setValues(savedData.values);
      setTeam(savedData.team);
    }
  }, []);

  const saveChanges = () => {
    const data = { aboutText, vision, mission, values, team };
    localStorage.setItem("aboutData", JSON.stringify(data));
    setEditMode(false);
  };

  const handleAddTeam = () => {
    if (!newName || !newRole || !newImage) return;

    const updated = [
      ...team,
      { id: Date.now(), name: newName, role: newRole, image: newImage }
    ];

    setTeam(updated);
    setNewName("");
    setNewRole("");
    setNewImage("");
  };

  const handleDelete = (id) => {
    const updated = team.filter((m) => m.id !== id);
    setTeam(updated);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => setNewImage(reader.result);
    if (file) reader.readAsDataURL(file);
  };

  return (
    <>
      <InnerHeader />
      <InnerHeaderBanner name="About Us" img={abtHeader} />

      <main>

        {/* EDIT BUTTON */}
        {isAdmin && (
          <div style={{ textAlign: "center", margin: "20px" }}>
            <button onClick={() => setEditMode(!editMode)}>
              {editMode ? "Cancel" : "Edit Page"}
            </button>

            {editMode && (
              <button onClick={saveChanges} style={{ marginLeft: "10px" }}>
                Save Changes
              </button>
            )}
          </div>
        )}

        {/* ABOUT SECTION */}
        <section className="about">
          <div className="container">
            <h2>Meyamgi Desam</h2>

            {editMode
              ? aboutText.map((text, i) => (
                  <textarea
                    key={i}
                    value={text}
                    onChange={(e) => {
                      const updated = [...aboutText];
                      updated[i] = e.target.value;
                      setAboutText(updated);
                    }}
                    style={{ width: "100%", marginBottom: "10px" }}
                  />
                ))
              : aboutText.map((text, i) => <p key={i}>{text}</p>)}
          </div>
        </section>

        {/* VISION SECTION */}
        <section className="vision">
          <div className="container">
            <h4>Vision</h4>
            {editMode ? (
              <textarea value={vision} onChange={(e) => setVision(e.target.value)} />
            ) : (
              <p>{vision}</p>
            )}

            <h4>Mission</h4>
            {editMode ? (
              <textarea value={mission} onChange={(e) => setMission(e.target.value)} />
            ) : (
              <p>{mission}</p>
            )}

            <h4>Our Values</h4>
            {editMode ? (
              <textarea value={values} onChange={(e) => setValues(e.target.value)} />
            ) : (
              <p>{values}</p>
            )}
          </div>
        </section>

        {/* TEAM SECTION */}
        <section className="management">
          <div className="container">
            <h2>Our Team</h2>

            {team.map((member) => (
              <div key={member.id} style={{ marginBottom: "20px" }}>
                <img src={member.image} width="120" alt={member.name} />
                <h4>{member.name}</h4>
                <p>{member.role}</p>

                {editMode && (
                  <button onClick={() => handleDelete(member.id)}>
                    Delete
                  </button>
                )}
              </div>
            ))}

            {/* ADD TEAM MEMBER */}
            {editMode && (
              <div style={{ marginTop: "20px" }}>
                <h4>Add Team Member</h4>
                <input
                  type="text"
                  placeholder="Name"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                />
                <input
                  type="text"
                  placeholder="Role"
                  value={newRole}
                  onChange={(e) => setNewRole(e.target.value)}
                />
                <input type="file" onChange={handleImageUpload} />
                <button onClick={handleAddTeam}>Add</button>
              </div>
            )}
          </div>
        </section>

      </main>

      <Footer />
    </>
  );
};

export default About;
