import React, { useState, useEffect } from "react";
import desam from "../img/desam.png";
import { Link, useLocation } from "react-router-dom";
import { FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";
import { animateScroll as scroll } from "react-scroll";

const InnerHeader = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isSticky, setIsSticky] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const location = useLocation();
  const splitLocation = location.pathname.split("/");

  const toTop = () => {
    scroll.scrollToTop({ duration: 0 });
    setIsOpen(false);
  };

  useEffect(() => {
    const handleScroll = () => setIsSticky(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    if (darkMode) document.body.classList.add("dark-mode");
    else document.body.classList.remove("dark-mode");
  }, [darkMode]);

  useEffect(() => {
    setIsOpen(false);
  }, [location]);

  useEffect(() => {
    document.body.style.overflow = isOpen ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [isOpen]);

  return (
    <>
      <header className={`header ${isSticky ? "sticky" : ""}`}>
        <div className="nav-container">
          <Link to="/" onClick={toTop}>
            <img src={desam} alt="DESAM" className="logo-img" />
          </Link>

          <nav className="desktop-nav">
            <Link to="/" className={splitLocation[1] === "" ? "active" : ""}>Home</Link>
            <Link to="/about" className={splitLocation[1] === "about" ? "active" : ""}>About Us</Link>
            <Link to="/services" className={splitLocation[1] === "services" ? "active" : ""}>Services</Link>
            <Link to="/careers" className={splitLocation[1] === "careers" ? "active" : ""}>Careers</Link>
            <Link to="/contact" className={splitLocation[1] === "contact" ? "active" : ""}>Contact Us</Link>
            <Link to="/AdminDashboard">Admin</Link>
          </nav>

          <div className="nav-controls">
            <button className="icon-btn" onClick={() => setDarkMode(!darkMode)}>
              {darkMode ? <FaSun /> : <FaMoon />}
            </button>

            <button className="hamburger" onClick={() => setIsOpen(!isOpen)}>
              {isOpen ? <FaTimes /> : <FaBars />}
            </button>
          </div>
        </div>
      </header>

      {/* FULL SCREEN MOBILE MENU */}
      <div className={`mobile-menu ${isOpen ? "open" : ""}`}>
        <Link to="/" onClick={() => setIsOpen(false)}>Home</Link>
        <Link to="/about" onClick={() => setIsOpen(false)}>About Us</Link>
        <Link to="/services" onClick={() => setIsOpen(false)}>Services</Link>
        <Link to="/careers" onClick={() => setIsOpen(false)}>Careers</Link>
        <Link to="/contact" onClick={() => setIsOpen(false)}>Contact Us</Link>
        <Link to="/AdminDashboard" onClick={() => setIsOpen(false)}>Admin</Link>
      </div>

      <style>{`
        body {
          margin:0;
          transition:0.3s ease;
        }

        body.dark-mode {
          background:#121212;
          color:white;
        }

        .header {
          position:fixed;
          top:0;
          width:100%;
          background:rgba(255,255,255,0.9);
          backdrop-filter:blur(15px);
          transition:0.3s ease;
          z-index:1000;
        }

        body.dark-mode .header {
          background:rgba(20,20,20,0.9);
        }

        .sticky {
          box-shadow:0 4px 20px rgba(0,0,0,0.1);
        }

        .nav-container {
          display:flex;
          justify-content:space-between;
          align-items:center;
          padding:10px 20px;
        }

        .logo-img {
          height:45px;
          width:auto;
          object-fit:contain;
          user-select:none;
          display:block;
        }

        .desktop-nav {
          display:flex;
          gap:25px;
        }

        .desktop-nav a {
          text-decoration:none;
          color:inherit;
          font-weight:500;
          transition:0.3s;
        }

        .desktop-nav a:hover,
        .active {
          color:#0d6efd;
          font-weight:600;
        }

        .nav-controls {
          display:flex;
          align-items:center;
        }

        .icon-btn,
        .hamburger {
          background:none;
          border:none;
          cursor:pointer;
        }

        .icon-btn {
          font-size:18px;
          margin-right:12px;
        }

        .hamburger {
          display:none;
          font-size:22px;
        }

        /* FULL SCREEN MOBILE MENU */
     .mobile-menu {
  position: fixed;
  top: 0;
  right: 0;              /* slide from right */
  width: 280px;          /* only menu panel width */
  height: 100vh;
  background: #ffffff;
  display: flex;
  flex-direction: column;
  padding-top: 90px;     /* space for header */
  box-shadow: -5px 0 20px rgba(0,0,0,0.1);
  transform: translateX(100%);
  transition: transform 0.4s cubic-bezier(.77,0,.18,1);
  z-index: 1001;
}

        body.dark-mode .mobile-menu {
          background:rgba(20,20,20,0.98);
        }

        .mobile-menu.open {
          transform:translateX(0);
        }

        .mobile-menu a {
          padding:18px 30px;
          text-decoration:none;
          color:inherit;
          font-size:18px;
          border-bottom:1px solid rgba(0,0,0,0.05);
          transition:0.3s ease;
        }

        .mobile-menu a:hover {
          background:rgba(13,110,253,0.1);
        }

        @media (max-width:768px) {
          .desktop-nav { display:none; }
          .hamburger { display:block; }
        }
      `}</style>
    </>
  );
};

export default InnerHeader;
