import React from "react";
import { Link } from "react-router-dom";
import cuetImg from "../img/cuet.jpg"; // change to your image path

const CuetSection = ({ isAuth }) => {
  return (
    <>
      <section className="py-5 bg-light">
        <div className="container">
          <div className="row align-items-center">

            {/* LEFT CONTENT */}
            <div className="col-lg-6 col-md-12 mb-4 mb-lg-0">
              <h2 className="fw-bold mb-3">CUET Coaching</h2>

              <p className="text-muted">
                #DESAM MUC na sinduna PG tougadaba maheiroi singidamak
                Common University Entrance Test ki Coaching Houdoikkhre.
                @Johnstone_Higher_secondary_School.
              </p>

              <p className="text-muted">
                Our abreast technology trends and unabating innovation has
                always enhanced the business impact for our clients resulting
                in happy faces seeing the revenue graph.
              </p>

              {/* BUTTON AREA */}
              <div className="d-flex gap-3 mt-4">
                <Link to="/cuet-details" className="btn btn-primary px-4">
                  READ MORE
                </Link>

                {/* üîê Admin Only Edit Button */}
                {isAuth && (
                  <Link
                    to="/admin/edit-cuet"
                    className="btn btn-danger px-4"
                  >
                    Edit
                  </Link>
                )}
              </div>
            </div>

            {/* RIGHT IMAGE */}
            <div className="col-lg-6 col-md-12">
              <div className="position-relative">
                <img
                  src={cuetImg}
                  alt="CUET Coaching"
                  className="img-fluid rounded shadow"
                />

                {/* üîê Floating Admin Edit Image Button */}
                {isAuth && (
                  <Link
                    to="/admin/edit-cuet"
                    className="btn btn-warning position-absolute top-0 end-0 m-3"
                  >
                    Edit Image
                  </Link>
                )}
              </div>
            </div>

          </div>
        </div>
      </section>
    </>
  );
};

export default CuetSection;
