import React, { useState } from "react";

function AdminDashboard() {

  const [activeSection, setActiveSection] = useState("dashboard");

  // Homepage state
  const [homepage, setHomepage] = useState({
    title: "Democratic Students' Alliance Of Manipur",
    motto: "UNITE AND STRUGGLE TO SURVIVE",
    description: "DESAM was established in 2002...",
    image: ""
  });

  // Profile state
  const [profile, setProfile] = useState({
    name: "Admin",
    email: "admin@gmail.com",
    photo: ""
  });

  // Image preview handlers
  const handleHomepageImage = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setHomepage({ ...homepage, image: URL.createObjectURL(file) });
  };

  const handleProfilePhoto = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setProfile({ ...profile, photo: URL.createObjectURL(file) });
  };

  return (
    <div className="container-fluid">
      <div className="row">

        {/* Sidebar */}
        <div className="col-md-2 bg-primary text-white vh-100 p-3">
          <h5>DESAM Admin</h5>
          <hr />
          <button className="btn btn-link text-white d-block"
            onClick={() => setActiveSection("dashboard")}>Dashboard</button>

          <button className="btn btn-link text-white d-block"
            onClick={() => setActiveSection("homepage")}>Homepage</button>

          <button className="btn btn-link text-white d-block"
            onClick={() => setActiveSection("profile")}>Profile</button>

          <button className="btn btn-link text-white d-block">
            Logout
          </button>
        </div>

        {/* Main Content */}
        <div className="col-md-10 p-4">

          {/* Dashboard */}
          {activeSection === "dashboard" && (
            <>
              <h3>Dashboard Overview</h3>
              <div className="row mt-4">
                <div className="col-md-4">
                  <div className="card shadow p-3 text-center">
                    <h5>Total Visitors</h5>
                    <h2>1200</h2>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="card shadow p-3 text-center">
                    <h5>Total Messages</h5>
                    <h2>85</h2>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="card shadow p-3 text-center">
                    <h5>Total Services</h5>
                    <h2>6</h2>
                  </div>
                </div>
              </div>
            </>
          )}

          {/* Homepage Editor */}
          {activeSection === "homepage" && (
            <>
              <h3>Edit Homepage</h3>
              <div className="card shadow p-4 mt-3">

                <div className="mb-3">
                  <label>Website Title</label>
                  <input
                    type="text"
                    className="form-control"
                    value={homepage.title}
                    onChange={(e) =>
                      setHomepage({ ...homepage, title: e.target.value })
                    }
                  />
                </div>

                <div className="mb-3">
                  <label>Motto</label>
                  <input
                    type="text"
                    className="form-control"
                    value={homepage.motto}
                    onChange={(e) =>
                      setHomepage({ ...homepage, motto: e.target.value })
                    }
                  />
                </div>

                <div className="mb-3">
                  <label>Description</label>
                  <textarea
                    className="form-control"
                    rows="3"
                    value={homepage.description}
                    onChange={(e) =>
                      setHomepage({ ...homepage, description: e.target.value })
                    }
                  />
                </div>

                <div className="mb-3">
                  <label>Upload Hero Image</label>
                  <input
                    type="file"
                    className="form-control"
                    onChange={handleHomepageImage}
                  />
                </div>

                {homepage.image && (
                  <img
                    src={homepage.image}
                    alt="Preview"
                    className="img-fluid mb-3"
                    style={{ maxHeight: "200px" }}
                  />
                )}

                <button className="btn btn-success">
                  Save Changes
                </button>

              </div>
            </>
          )}

          {/* Profile Section */}
          {activeSection === "profile" && (
            <>
              <h3>Admin Profile</h3>
              <div className="card shadow p-4 mt-3 text-center">

                <img
                  src={profile.photo || "https://via.placeholder.com/120"}
                  alt="Profile"
                  className="rounded-circle mb-3"
                  width="120"
                  height="120"
                  style={{ objectFit: "cover" }}
                />

                <input
                  type="file"
                  className="form-control mb-3"
                  onChange={handleProfilePhoto}
                />

                <div className="mb-3">
                  <label>Name</label>
                  <input
                    type="text"
                    className="form-control"
                    value={profile.name}
                    onChange={(e) =>
                      setProfile({ ...profile, name: e.target.value })
                    }
                  />
                </div>

                <div className="mb-3">
                  <label>Email</label>
                  <input
                    type="email"
                    className="form-control"
                    value={profile.email}
                    onChange={(e) =>
                      setProfile({ ...profile, email: e.target.value })
                    }
                  />
                </div>

                <button className="btn btn-success">
                  Update Profile
                </button>

              </div>
            </>
          )}

        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
