import React, { useState, useEffect, useRef } from "react";
import desam from "../img/desam.png";
import { Link, useLocation } from "react-router-dom";
import { FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";
import { animateScroll as scroll } from "react-scroll";

const InnerHeader = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isSticky, setIsSticky] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const location = useLocation();
  const splitLocation = location.pathname.split("/");
  const headerRef = useRef(null);

  const toTop = () => {
    scroll.scrollToTop({ duration: 0 });
    setIsOpen(false);
  };

  // Sticky header
  useEffect(() => {
    const handleScroll = () => setIsSticky(window.scrollY > 80);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Dark mode
  useEffect(() => {
    if (darkMode) document.body.classList.add("dark-mode");
    else document.body.classList.remove("dark-mode");
  }, [darkMode]);

  // Close mobile menu on route change
  useEffect(() => {
    setIsOpen(false);
  }, [location]);

  // Lock scroll when menu open
  useEffect(() => {
    if (isOpen) {
      document.body.classList.add("menu-open");
    } else {
      document.body.classList.remove("menu-open");
    }

    return () => {
      document.body.classList.remove("menu-open");
    };
  }, [isOpen]);

  return (
    <>
      <header ref={headerRef} className={`header ${isSticky ? "sticky" : ""}`}>
        <div className="nav-container">
          <Link to="/" onClick={toTop}>
            <img src={desam} alt="DESAM" className="logo-img" />
          </Link>

          {/* Desktop Nav */}
          <nav className="desktop-nav">
            <Link to="/" className={splitLocation[1] === "" ? "active" : ""}>Home</Link>
            <Link to="/about" className={splitLocation[1] === "about" ? "active" : ""}>About Us</Link>
            <Link to="/services" className={splitLocation[1] === "services" ? "active" : ""}>Services</Link>
            <Link to="/careers" className={splitLocation[1] === "careers" ? "active" : ""}>Careers</Link>
            <Link to="/contact" className={splitLocation[1] === "contact" ? "active" : ""}>Contact Us</Link>
            <Link to="/AdminDashboard">Admin</Link>
          </nav>

          <div className="nav-controls">
            <button className="icon-btn" onClick={() => setDarkMode(!darkMode)}>
              {darkMode ? <FaSun /> : <FaMoon />}
            </button>

            <button className="hamburger" onClick={() => setIsOpen(!isOpen)}>
              {isOpen ? <FaTimes /> : <FaBars />}
            </button>
          </div>
        </div>
      </header>

      {/* Overlay */}
      <div
        className={`overlay ${isOpen ? "show" : ""}`}
        onClick={() => setIsOpen(false)}
      />

      {/* Mobile Menu */}
      <div className={`mobile-menu ${isOpen ? "open" : ""}`}>
        <Link to="/" onClick={() => setIsOpen(false)}>Home</Link>
        <Link to="/about" onClick={() => setIsOpen(false)}>About Us</Link>
        <Link to="/services" onClick={() => setIsOpen(false)}>Services</Link>
        <Link to="/careers" onClick={() => setIsOpen(false)}>Careers</Link>
        <Link to="/contact" onClick={() => setIsOpen(false)}>Contact Us</Link>
        <Link to="/AdminDashboard" onClick={() => setIsOpen(false)}>Admin</Link>
      </div>

      <style>{`
        body { margin:0; transition:0.3s ease; }
        body.dark-mode { background:#121212; color:white; }
        body.menu-open { overflow:hidden; }

        .header {
          position:fixed;
          top:0;
          width:100%;
          backdrop-filter:blur(15px);
          background:rgba(255,255,255,0.85);
          transition:all 0.3s ease;
          z-index:1000;
        }

        body.dark-mode .header {
          background:rgba(20,20,20,0.9);
        }

        .sticky {
          box-shadow:0 4px 20px rgba(0,0,0,0.15);
        }

        .nav-container {
          display:flex;
          justify-content:space-between;
          align-items:center;
          padding:15px 25px;
        }

        .logo-img {
          width:70px;
          height:40px;
        }

        .desktop-nav {
          display:flex;
          gap:25px;
        }

        .desktop-nav a {
          text-decoration:none;
          color:inherit;
          font-weight:500;
          transition:0.3s;
        }

        .desktop-nav a:hover,
        .active {
          color:#0d6efd;
          font-weight:bold;
        }

        .nav-controls {
          display:flex;
          align-items:center;
        }

        .icon-btn,
        .hamburger {
          background:none;
          border:none;
          cursor:pointer;
        }

        .icon-btn {
          font-size:18px;
          margin-right:12px;
        }

        .hamburger {
          display:none;
          font-size:24px;
        }

        .overlay {
          position:fixed;
          inset:0;
          background:rgba(0,0,0,0.45);
          opacity:0;
          visibility:hidden;
          transition:0.3s;
          z-index:900;
        }

        .overlay.show {
          opacity:1;
          visibility:visible;
        }

        .mobile-menu {
          position:fixed;
          top:0;
          right:0;
          width:80%;
          max-width:320px;
          height:100vh;
          background:rgba(255,255,255,0.98);
          backdrop-filter:blur(20px);
          display:flex;
          flex-direction:column;
          padding-top:90px;
          transform:translateX(100%);
          transition:transform 0.4s cubic-bezier(.77,0,.18,1);
          z-index:1001;
        }

        body.dark-mode .mobile-menu {
          background:rgba(20,20,20,0.98);
        }

        .mobile-menu.open {
          transform:translateX(0);
        }

        .mobile-menu a {
          padding:18px 25px;
          text-decoration:none;
          color:inherit;
          font-size:16px;
          border-bottom:1px solid rgba(0,0,0,0.05);
          transition:0.3s;
        }

        .mobile-menu a:hover {
          background:rgba(13,110,253,0.1);
        }

        @media (max-width:768px) {
          .desktop-nav { display:none; }
          .hamburger { display:block; }
        }

        @media (max-width:480px) {
          .nav-container { padding:12px 18px; }
          .logo-img { width:120px; }
          .mobile-menu { width:85%; }
        }
      `}</style>
    </>
  );
};

export default InnerHeader;
