'use strict';

var Day = require('es-abstract/5/Day');
var DayFromYear = require('es-abstract/5/DayFromYear');
var YearFromTime = require('es-abstract/5/YearFromTime');

// https://262.ecma-international.org/5.1/#sec-15.9.1.4

module.exports = function DayWithinYear(t) {
	return Day(t) - DayFromYear(YearFromTime(t));
};
