'use strict';

var GetIntrinsic = require('get-intrinsic');

var $DateUTC = GetIntrinsic('%Date.UTC%');

var $isFinite = require('es-abstract/helpers/isFinite');

var DateFromTime = require('es-abstract/5/DateFromTime');
var Day = require('es-abstract/5/Day');
var floor = require('es-abstract/5/floor');
var modulo = require('es-abstract/5/modulo');
var MonthFromTime = require('es-abstract/5/MonthFromTime');
var ToInteger = require('es-abstract/5/ToInteger');
var YearFromTime = require('es-abstract/5/YearFromTime');

// https://262.ecma-international.org/5.1/#sec-15.9.1.12

module.exports = function MakeDay(year, month, date) {
	if (!$isFinite(year) || !$isFinite(month) || !$isFinite(date)) {
		return NaN;
	}
	var y = ToInteger(year);
	var m = ToInteger(month);
	var dt = ToInteger(date);
	var ym = y + floor(m / 12);
	var mn = modulo(m, 12);
	var t = $DateUTC(ym, mn, 1);
	if (YearFromTime(t) !== ym || MonthFromTime(t) !== mn || DateFromTime(t) !== 1) {
		return NaN;
	}
	return Day(t) + dt - 1;
};
